import React, { useState } from 'react'
import axios  from 'axios'

export default function Login() {
  const [username, setUsername] = useState<string>("")
  const [password, setPassword] = useState<string>("")
  
  const login = () => {
    axios.post("http://localhost:8000/login", {
      username,
      password
    }, {
      withCredentials: true 
    }).then(res => {
      console.log(res.data);
    })
  }
  const getUser = () => {
    axios.get("http://localhost:8000/user", {
    withCredentials: true
    }).then(res => {
      console.log(res.data);
    })
  }

  return (
    <div>
      <h1>Login</h1>
      {/* <input type="text" placeholder='username' onChange={e => setUsername(e.target.value)} /> */}
      <input type="text" placeholder='password' onChange={e => setPassword(e.target.value)} />
      <button onClick={login}>Login</button>
      <button onClick={getUser}>Mengecek Data User Login</button>
  <div className="kotak_login">
  <p className="tulisan_login">Silahkan login</p>
  <form>
    <label>Username</label>
    <input type="text" name="username" className="form_login"placeholder="username" onChange={e => setUsername(e.target.value)} />
    <label>Password</label>
    <input type="text" name="password" className="form_login"placeholder="password" onChange={e => setPassword(e.target.value)} /> 
    <button type="submit" className="tombol_login" defaultValue="LOGIN" />
    <br />
    <br />
    <center>
      <a className="link" href="https://www.malasngoding.com">
        kembali
      </a>
    </center>
  </form>
</div>
    </div>
  )
}
