import React, { useState } from 'react'
import axios  from 'axios'
import './login.css'

export default function Login() {
  const [username, setUsername] = useState<string>("")
  const [password, setPassword] = useState<string>("")
  
  const login = () => {
    axios.post("http://localhost:8000/login", {
      username,
      password
    }, {
      withCredentials: true 
    }).then(res => {
      console.log(res.data);
    })
  }
  return (
    <>
      <div className="Auth-form-container">
      <form className="Auth-form">
        <div className="Auth-form-content">
          <h3 className="Auth-form-title">Login</h3>
          <div className="form-group">
            <label>username</label>
            <input type="email" className="form-control" placeholder="masukan username" onChange={e => setUsername(e.target.value)} />
          </div>
          <div className="form-group mt-3">
            <label>Password</label>
            <input type="password" className="form-control mt-1" placeholder="Enter password"  onChange={e => setPassword(e.target.value)} />
          </div>
          <div className="d-grid gap-2 mt-3">
            <button type="submit" className="btn btn-primary"onClick={login}>Login</button>
          </div>
        </div>
      </form>
    </div>
    </>
  )
}
